var gesperrt = false;
 
exports.appsperr = function(userid,operation,callback) {
 
 
if(userid == "Lehrer"){
 
	if(operation == "sperren"){
	 	gesperrt = true;    	 
		callback({'response':"Apps gesperrt",'res':true,});
	 
	}else{
	 	gesperrt = false;
		callback({'response':"Apps entsperrt",'res':false});
	 
	}
}else if (userid == "Schueler") {
 
	if(operation == "check"){
	 	if(gesperrt == true){
			 callback({'response':"Apps gesperrt",'res':true,});
		 } else {
			 callback({'response':"Apps entsperrt",'res':false});
		 }
	}
}
}