var neueNachricht = false;
 
exports.filesend = function(userid,operation,callback) {
 
 
if(userid == "Lehrer"){
 
	if(operation == "senden"){
	 	neueNachricht = true;    	 
		callback({'response':"Datei gesendet",'res':true,});
	 
	}
}else if (userid == "Schueler") {
 
	if(operation == "check"){
	 	if(neueNachricht == true){
			 callback({'response':"Datei gefunden",'res':true,});
		 } else {
			 callback({'response':"keine neue Nachricht",'res':false});
		 }
	}
	if(operation == "gelesen") {
			 neueNachricht = false;
			 callback({'response':"Nachricht gelesen",'res':false});
		 }
}
}