var testid = 'Admin';
var testpassword = '1234';
 
exports.login = function(userid,password,callback) {
 
 
if(userid == testid){
 
	if(password == testpassword){
	 
		callback({'response':"Login erfolgreich",'res':true,});
	 
	}else{
	 
		callback({'response':"falsches Passwort",'res':false});
	 
	}
}else {
 
	callback({'response':"Benutzer existiert nicht",'res':false});
 
}

}