var testid1 = 'Lehrer';
var testpassword1 = '1234';
var testid2 = 'Schueler';
var testpassword2 = '1234';
 
exports.login = function(userid,password,callback) {
 
 
if(userid == testid1){
 
	if(password == testpassword1){
	 
		callback({'response':"Lehrer",'res':true,});
	 
	}else{
	 
		callback({'response':"falsches Passwort",'res':false});
	 
	}
}else if (userid == testid2) {
 
	if(password == testpassword2){
	 
		callback({'response':"Schueler",'res':true,});
	 
	}else{
	 
		callback({'response':"falsches Passwort",'res':false});
	 
	}
} else {	
	callback({'response':"Benutzer existiert nicht",'res':false});
 
}

}